import{_ as a,c as n,o as t,z as e,a as o}from"./chunks/framework.8e4cc2b6.js";const _=JSON.parse('{"title":"Hot fixable dialog understanding by everyone","description":"","frontmatter":{},"headers":[],"relativePath":"essentials/newformulation.md","lastUpdated":1680749255000}'),i={name:"essentials/newformulation.md"},s=e("h1",{id:"hot-fixable-dialog-understanding-by-everyone",tabindex:"-1"},[o("Hot fixable dialog understanding by everyone "),e("a",{class:"header-anchor",href:"#hot-fixable-dialog-understanding-by-everyone","aria-label":'Permalink to "Hot fixable dialog understanding by everyone"'},"â€‹")],-1),r=e("p",null,'One of the main design goals of OpenCUI is to provide state-of-the-art dialog understanding capabilities with a great developer experience. By "developer," we mean the existing business application developer, and we do not assume that they have a background in machine learning or natural language understanding. Ideally, it should be possible for these developers to build and fix the dialog understanding capabilities without going through a steep learning curve.',-1),d=e("p",null,"Formulating dialog understanding as traditional intent classification and named entity recognition has many drawbacks in practice. Therefore, OpenCUI adopts a set of new task formulations. Instead of focusing on literal meanings, these new formulations aim to convert user utterances into implied meanings in the context of the services that the business provides. This can greatly simplify the development of the downstream dialog management module. Large language model-based solutions for these new formulations offer excellent zero-shot/few-shot capabilities, making them much more production-friendly.",-1),l=[s,r,d];function c(g,h,u,f,m,p){return t(),n("div",null,l)}const v=a(i,[["render",c]]);export{_ as __pageData,v as default};
