<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#F78C6C;">END</span><span style="color:#A6ACCD;">) </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> (</span><span style="color:#F78C6C;">SELECT</span><span style="color:#A6ACCD;"> EXTRACT(ISODOW </span><span style="color:#F78C6C;">FROM</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">date</span><span style="color:#A6ACCD;">))::</span><span style="color:#C792EA;">text</span></span>
